mapper.update:
UPDATE lb_order lb
SET lb.status = IFNULL(#{ss}, -2)
, lb.reason = #{reason}
, lb.end_time = NOW()
WHERE id = #{orderId}
AND lb.status = -1

UPDATE lb_borrow
SET status = #{status},
create_time = NOW()
WHERE book_id = #{bookId}
AND order_id = #{orderId}

mapper.select:
SELECT 1 FROM lb_borrow lb INNER JOIN lb_order lo ON lo.id = lb.order_id WHERE lo.card_id= #{query} AND lb.status = 0 AND lb.returnTime >=NOW() AND lb.book_id in
#{item}
limit 1
