mapper.update:
UPDATE lb_card
SET total = #{total}
WHERE userId = #{id}
AND #{total} > borrowed

mapper.select:
SELECT l.id, l.name, l.permissions, lc.id as cardId, lc.borrowed, lc.total
FROM lb_user l
LEFT JOIN lb_card lc ON lc.userId = l.id


        WHERE l.id > #{pageInfo.id}
        ORDER BY l.id
        LIMIT #{pageInfo.pageSize}


        ORDER BY l.id
        LIMIT
        #{pageInfo.offset}
        ,
        #{pageInfo.pageSize}

mapper.update:
UPDATE lb_account
SET status = #{status}
WHERE id = #{id}
AND (SELECT 1 FROM lb_user lu WHERE lu.id = #{id} AND lu.permissions != "admin")

UPDATE lb_user
SET permissions = #{notSafe}
WHERE id = #{id}
