mapper.select:
SELECT id, name, permissions
FROM lb_user
WHERE


        id=#{id}


        name=#{name}

SELECT id, name, image
FROM lb_user
WHERE id = #{id}

SELECT id, total, borrowed, 0 as timeout
FROM lb_card lc
WHERE lc.userId = #{id}

SELECT id
FROM lb_card lu
WHERE lu.userId = #{id}

mapper.update:
UPDATE lb_user
SET name = #{name}
WHERE id = #{id}

mapper.select:
SELECT la.id, la.status,lu.name
FROM lb_account la
LEFT JOIN lb_user lu ON la.id = lu.id
WHERE lu.permissions != "admin"


        AND la.status = #{status}


LIMIT #{pageInfo.offset},#{pageInfo.pageSize}

SELECT COUNT(la.id)
FROM lb_account la
LEFT JOIN lb_user lu ON la.id = lu.id
WHERE lu.permissions != "admin"


        AND la.status = #{status}


LIMIT #{pageInfo.offset},#{pageInfo.pageSize}
